import React, { useEffect, useState } from 'react';
import './App.css';
import { gapi } from 'gapi-script';

// Event component to display individual events
function Event({ description }) {
  return (
    <div className="event-container">
      <p>{description}</p>
    </div>
  );
}

function App() {
  const [events, setEvents] = useState([]);
  const calendarID = process.env.REACT_APP_CALENDAR_ID;
  const apiKey = process.env.REACT_APP_GOOGLE_API_KEY;
  const accessToken = process.env.REACT_APP_GOOGLE_ACCESS_TOKEN;

  const getEvents = (calendarID, apiKey) => {
    function initiate() {
      gapi.client
        .init({
          apiKey: apiKey,
        })
        .then(function () {
          return gapi.client.request({
            path: `https://www.googleapis.com/calendar/v3/calendars/${calendarID}/events`,
          });
        })
        .then(
          (response) => {
            let events = response.result.items;
            setEvents(events);
          },
          function (err) {
            console.error("Error fetching events", err);
            return [false, err];
          }
        );
    }
    gapi.load('client', initiate);
  };

  // Create event function
  const createEvent = (event) => {
    const eventData = {
      'summary': event.summary,
      'description': event.description,
      'start': {
        'dateTime': event.startDateTime,
        'timeZone': 'America/Los_Angeles'
      },
      'end': {
        'dateTime': event.endDateTime,
        'timeZone': 'America/Los_Angeles'
      }
    };

    function initiate() {
      gapi.client
        .init({
          apiKey: apiKey,
        })
        .then(function() {
          gapi.auth.setToken({
            access_token: accessToken
          });

          return gapi.client.request({
            path: `https://www.googleapis.com/calendar/v3/calendars/${calendarID}/events`,
            method: 'POST',
            body: eventData
          });
        })
        .then(
          (response) => {
            console.log('Event created:', response);
            // Refresh events list
            getEvents(calendarID, apiKey);
          },
          function(err) {
            console.error('Error creating event:', err);
          }
        );
    }

    gapi.load('client', initiate);
  };

  useEffect(() => {
    getEvents(calendarID, apiKey);
  }, []);

  return (
    <div className="App flex flex-col justify-center py-8">
      <h1 className="mb-4 text-2xl font-bold">
        React App with Google Calendar API!
      </h1>
      <ul>
        {events?.map((event) => (
          <li key={event.id} className="flex justify-center">
            <Event description={event.summary} />
          </li>
        ))}
      </ul>

      {/* Simple form to create a new event */}
      <div className="event-form">
        <h2>Create New Event</h2>
        <form onSubmit={(e) => {
          e.preventDefault();
          const formData = new FormData(e.target);
          createEvent({
            summary: formData.get('summary'),
            description: formData.get('description'),
            startDateTime: formData.get('start'),
            endDateTime: formData.get('end')
          });
          e.target.reset();
        }}>
          <input type="text" name="summary" placeholder="Event Title" required />
          <textarea name="description" placeholder="Description"></textarea>
          <input type="datetime-local" name="start" required />
          <input type="datetime-local" name="end" required />
          <button type="submit">Create Event</button>
        </form>
      </div>
    </div>
  );
}

export default App;
