import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

// New component for natural language input
function NaturalLanguageForm() {
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;

    setLoading(true);
    setError("");
    setMessage("");

    try {
      const response = await axios.post('http://127.0.0.1:5000/api/natural-language-event', {
        text
      });

      if (response.data.success) {
        setMessage(`Event created! View it here: ${response.data.eventLink}`);
        setText(""); // Clear the input
      } else {
        setError(`Error: ${response.data.message}`);
      }
    } catch (error) {
      setError(`Error: ${error.response?.data?.message || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="natural-language-form">
      <h2>Create Event with Natural Language</h2>

      {error && <div className="error-message">{error}</div>}
      {message && <div className="success-message">{message}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Describe your event:</label>
          <textarea 
            placeholder="Example: I have a CMPT105 assignment due on Friday night"
            value={text} 
            onChange={(e) => setText(e.target.value)}
            disabled={loading}
            rows={3}
            required
          />
        </div>

        <button type="submit" disabled={loading} className="submit-button">
          {loading ? 'Creating...' : 'Create Event'}
        </button>
      </form>
    </div>
  );
}

function CalendarForm() {
  // Original CalendarForm component code...
  // Keep all your existing code for the manual form
}

function App() {
  return (
    <div className="App">
      <NaturalLanguageForm />
      <hr />
      <CalendarForm />
    </div>
  );
}

export default App;
