import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function CalendarForm() { 
  const [eventData, setEventData] = useState({
    summary: 'My Event',
    location: 'Online',
    description: 'Event description',
    start: {
      dateTime: '2025-03-10T00:00:00-07:00',
      timeZone: 'America/Vancouver',
    },
    end: {
      dateTime: '2025-03-10T08:00:00-07:00',
      timeZone: 'America/Vancouver',
    },
    calendarId: 'primary', // Default to primary calendar
    duration: '01:00', // Default 1 hour duration
    colorId: '' // Will be set based on calendar
  });

  const [calendars, setCalendars] = useState([]);
  const [calendarColors, setCalendarColors] = useState({});
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  // Recurrence state
  const [recurrenceType, setRecurrenceType] = useState('none');
  const [recurrenceFreq, setRecurrenceFreq] = useState('DAILY');
  const [recurrenceCount, setRecurrenceCount] = useState(3);
  const [recurrenceDays, setRecurrenceDays] = useState([]);

  // Update recurrence rules when settings change
  useEffect(() => {
    if (recurrenceType === 'none') {
      setEventData(prev => ({ ...prev, recurrence: null }));
      return;
    }

    let rrule = `RRULE:FREQ=${recurrenceFreq}`;

    if (recurrenceCount) {
      rrule += `;COUNT=${recurrenceCount}`;
    }

    if (recurrenceFreq === 'WEEKLY' && recurrenceDays.length > 0) {
      rrule += `;BYDAY=${recurrenceDays.join(',')}`;
    }

    setEventData(prev => ({ ...prev, recurrence: [rrule] }));
  }, [recurrenceType, recurrenceFreq, recurrenceCount, recurrenceDays]);

  // Fetch available calendars and colors when component mounts
  useEffect(() => {
    const fetchCalendarsAndColors = async () => {
      try {
        // Fetch calendars
        const calendarResponse = await axios.get('http://127.0.0.1:5000/api/get-calendars');
        if (calendarResponse.data.success) {
          setCalendars(calendarResponse.data.calendars);

          // If there's a primary calendar, set it as default
          const primaryCalendar = calendarResponse.data.calendars.find(cal => cal.primary);
          if (primaryCalendar) {
            setEventData(prev => ({
              ...prev,
              calendarId: primaryCalendar.id,
              colorId: primaryCalendar.colorId // Set event color to match calendar
            }));
          }
        } else {
          setMessage(`Error: ${calendarResponse.data.message}`);
        }

        // Fetch colors
        const colorsResponse = await axios.get('http://127.0.0.1:5000/api/get-calendar-colors');
        if (colorsResponse.data.success) {
          setCalendarColors(colorsResponse.data.colors);
        } else {
          setMessage(`Error: ${colorsResponse.data.message}`);
        }
      } catch (error) {
        setMessage(`Error fetching data: ${error.message}`);
      }
    };

    fetchCalendarsAndColors();
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === 'calendarId') {
      const selectedCalendar = calendars.find(cal => cal.id === value);
      setEventData({
        ...eventData,
        calendarId: value,
        colorId: selectedCalendar?.colorId // Auto-match event color to calendar
      });
    } else {
      setEventData({
        ...eventData,
        [name]: value
      });
    }
  };

  // Handle date/time changes
  const handleDateTimeChange = (e) => {
    const { name, value } = e.target;
    const [field, subfield] = name.split('.');

    setEventData({
      ...eventData,
      [field]: {
        ...eventData[field],
        [subfield]: value
      }
    });
  };

  // Submit form to Flask backend
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Send data to Flask API, including the selected calendar ID
      const response = await axios.post('http://127.0.0.1:5000/api/create-event', eventData);

      if (response.data.success) {
        setMessage(`Event created! View it here: ${response.data.eventLink}`);
      } else {
        setMessage(`Error: ${response.data.message}`);
      }
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="calendar-form">
      <h2>Create Calendar Event</h2>
      <form onSubmit={handleSubmit}>
        {/* Calendar Selection Dropdown */}
        <div className="form-group">
          <label>Calendar:</label>
          <select 
            name="calendarId" 
            value={eventData.calendarId} 
            onChange={handleChange}
          >
            {calendars.map(calendar => (
              <option 
                key={calendar.id} 
                value={calendar.id}
                style={{ 
                  backgroundColor: calendar.backgroundColor,
                  color: '#fff'
                }}
              >
                {calendar.summary} {calendar.primary ? '(Primary)' : ''}
              </option>
            ))}
          </select>
        </div>

        {/* Event Title */}
        <div className="form-group">
          <label>Title:</label>
          <input 
            type="text" 
            name="summary" 
            value={eventData.summary} 
            onChange={handleChange} 
          />
        </div>

        {/* Event Location */}
        <div className="form-group">
          <label>Location:</label>
          <input 
            type="text" 
            name="location" 
            value={eventData.location} 
            onChange={handleChange} 
          />
        </div>

        {/* Event Description */}
        <div className="form-group">
          <label>Description:</label>
          <textarea 
            name="description" 
            value={eventData.description} 
            onChange={handleChange} 
          />
        </div>

        {/* Event Start Date/Time */}
        <div className="form-group">
          <label>Start Date and Time:</label>
          <input 
            type="datetime-local" 
            name="start.dateTime" 
            value={eventData.start.dateTime.slice(0, 16)} 
            onChange={handleDateTimeChange} 
          />
        </div>

        {/* Event Duration */}
        <div className="form-group">
          <label>Duration (HH:MM):</label>
          <input 
            type="text" 
            name="duration" 
            placeholder="HH:MM"
            value={eventData.duration} 
            onChange={handleChange} 
          />
        </div>

        {/* Event Color */}
        <div className="form-group">
          <label>Event Color:</label>
          <select 
            name="colorId" 
            value={eventData.colorId || ''} 
            onChange={handleChange}
            style={{ 
              backgroundColor: calendarColors?.event?.[eventData.colorId]?.background || 'white',
              color: calendarColors?.event?.[eventData.colorId]?.foreground || 'black'
            }}
          >
            <option value="">Use calendar color</option>
            {calendarColors?.event && Object.entries(calendarColors.event).map(([id, color]) => (
              <option 
                key={id} 
                value={id}
                style={{ 
                  backgroundColor: color.background, 
                  color: color.foreground 
                }}
              >
                Color {id}
              </option>
            ))}
          </select>
        </div>

        {/* Event Repetition */}
        <div className="form-group">
          <label>Repetition:</label>
          <select 
            value={recurrenceType} 
            onChange={(e) => setRecurrenceType(e.target.value)}
          >
            <option value="none">No repeat</option>
            <option value="repeat">Repeat</option>
          </select>

          {recurrenceType === 'repeat' && (
            <div className="recurrence-options">
              <div className="form-group">
                <label>Frequency:</label>
                <select 
                  value={recurrenceFreq} 
                  onChange={(e) => setRecurrenceFreq(e.target.value)}
                >
                  <option value="DAILY">Daily</option>
                  <option value="WEEKLY">Weekly</option>
                  <option value="MONTHLY">Monthly</option>
                  <option value="YEARLY">Yearly</option>
                </select>
              </div>

              <div className="form-group">
                <label>Repeat:</label>
                <input 
                  type="number" 
                  min="1" 
                  max="99"
                  value={recurrenceCount} 
                  onChange={(e) => setRecurrenceCount(e.target.value)}
                /> times
              </div>

              {recurrenceFreq === 'WEEKLY' && (
                <div className="weekday-selector">
                  <label>On days:</label>
                  <div className="weekday-checkboxes">
                    {[
                      {day: 'MO', label: 'Monday'},
                      {day: 'TU', label: 'Tuesday'},
                      {day: 'WE', label: 'Wednesday'},
                      {day: 'TH', label: 'Thursday'},
                      {day: 'FR', label: 'Friday'},
                      {day: 'SA', label: 'Saturday'},
                      {day: 'SU', label: 'Sunday'}
                    ].map(({day, label}) => (
                      <label key={day} className="weekday-checkbox">
                        <input 
                          type="checkbox" 
                          checked={recurrenceDays.includes(day)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setRecurrenceDays([...recurrenceDays, day]);
                            } else {
                              setRecurrenceDays(recurrenceDays.filter(d => d !== day));
                            }
                          }}
                        />
                        {label}
                      </label>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Creating...' : 'Create Event'}
        </button>
      </form>

      {message && <div className="message">{message}</div>}
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <CalendarForm />
    </div>
  );
}

export default App;
