import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CalendarForm() {
  const [eventData, setEventData] = useState({
    summary: 'My Event',
    location: 'Online',
    description: 'Event description',
    start: {
      dateTime: '2025-03-10T00:00:00-07:00',
      timeZone: 'America/Vancouver',
    },
    end: {
      dateTime: '2025-03-10T08:00:00-07:00',
      timeZone: 'America/Vancouver',
    },
    calendarId: 'primary' // Default to primary calendar
  });

  const [calendars, setCalendars] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  // Fetch available calendars when component mounts
  useEffect(() => {
    const fetchCalendars = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/get-calendars');
        if (response.data.success) {
          setCalendars(response.data.calendars);

          // If there's a primary calendar, set it as default
          const primaryCalendar = response.data.calendars.find(cal => cal.primary);
          if (primaryCalendar) {
            setEventData(prev => ({
              ...prev,
              calendarId: primaryCalendar.id
            }));
          }
        } else {
          setMessage(`Error: ${response.data.message}`);
        }
      } catch (error) {
        setMessage(`Error fetching calendars: ${error.message}`);
      }
    };

    fetchCalendars();
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setEventData({
      ...eventData,
      [name]: value
    });
  };

  // Submit form to Flask backend
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Send data to Flask API, including the selected calendar ID
      const response = await axios.post('http://localhost:5000/api/create-event', eventData);

      if (response.data.success) {
        setMessage(`Event created! View it here: ${response.data.eventLink}`);
      } else {
        setMessage(`Error: ${response.data.message}`);
      }
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Create Calendar Event</h2>
      <form onSubmit={handleSubmit}>
        {/* Calendar Selection Dropdown */}
        <div>
          <label>Calendar:</label>
          <select 
            name="calendarId" 
            value={eventData.calendarId} 
            onChange={handleChange}
          >
            {calendars.map(calendar => (
              <option 
                key={calendar.id} 
                value={calendar.id}
                style={{ 
                  backgroundColor: calendar.backgroundColor,
                  color: '#fff'
                }}
              >
                {calendar.summary} {calendar.primary ? '(Primary)' : ''}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Title:</label>
          <input 
            type="text" 
            name="summary" 
            value={eventData.summary} 
            onChange={handleChange} 
          />
        </div>

        <div>
          <label>Location:</label>
          <input 
            type="text" 
            name="location" 
            value={eventData.location} 
            onChange={handleChange} 
          />
        </div>

        <div>
          <label>Description:</label>
          <textarea 
            name="description" 
            value={eventData.description} 
            onChange={handleChange} 
          />
        </div>

        {/* Add more fields for dates, times, etc. */}

        <button type="submit" disabled={loading}>
          {loading ? 'Creating...' : 'Create Event'}
        </button>
      </form>

      {message && <div className="message">{message}</div>}
    </div>
  );
}

export default CalendarForm;
