import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

// Chat Message Component
const ChatMessage = ({ message, type }) => {
  return (
    <div className={`chat-message ${type}`}>
      <div className="message-content">
        {message}
      </div>
    </div>
  );
};

// Calendar Selector Component
const CalendarSelector = ({ calendars, selectedCalendar, onSelect }) => {
  return (
    <div className="calendar-selector">
      <select
        value={selectedCalendar}
        onChange={(e) => onSelect(e.target.value)}
        className="calendar-select"
      >
        {calendars.map((calendar) => (
          <option key={calendar.id} value={calendar.id}>
            {calendar.summary}
          </option>
        ))}
      </select>
    </div>
  );
};

// Free Time Display Component
const FreeTimeDisplay = ({ freeSlots }) => {
  if (!freeSlots || freeSlots.length === 0) {
    return <div className="no-free-time">No free time slots found.</div>;
  }

  return (
    <div className="free-time-display">
      <h4>Available Time Slots:</h4>
      <ul className="free-slots-list">
        {freeSlots.map((slot, index) => {
          const startTime = new Date(slot.start).toLocaleString();
          const endTime = new Date(slot.end).toLocaleString();
          const duration = Math.round(slot.duration_minutes);

          return (
            <li key={index} className="free-slot-item">
              <span className="slot-time">{startTime} - {endTime}</span>
              <span className="slot-duration">({duration} minutes)</span>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

// Event List Component
const EventList = ({ events }) => {
  if (!events || events.length === 0) {
    return <div className="no-events">No events found.</div>;
  }

  return (
    <div className="event-list">
      <h4>Events:</h4>
      <ul className="events-list">
        {events.map((event, index) => {
          const startTime = new Date(event.start).toLocaleString();

          return (
            <li key={index} className="event-item">
              <div className="event-summary">{event.summary}</div>
              <div className="event-time">{startTime}</div>
              {event.location && <div className="event-location">üìç {event.location}</div>}
              {event.htmlLink && (
                <a href={event.htmlLink} target="_blank" rel="noopener noreferrer" className="event-link">
                  View in Calendar
                </a>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
};

// Main Calendar Chat Component
function CalendarChat() {
  const [inputText, setInputText] = useState("");
  const [loading, setLoading] = useState(false);
  const [messages, setMessages] = useState([
    { type: 'assistant', content: "Hi! I'm your calendar assistant. How can I help you today?" }
  ]);
  const [calendars, setCalendars] = useState([]);
  const [selectedCalendar, setSelectedCalendar] = useState('primary');
  const [colors, setColors] = useState({});
  const [context, setContext] = useState({
    selectedCalendar: 'primary',
    recentQueries: []
  });

  const messagesEndRef = useRef(null);

  // Fetch calendars on component mount
  useEffect(() => {
    const fetchCalendars = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5000/api/get-calendars');
        if (response.data.success) {
          setCalendars(response.data.calendars);

          // Set primary calendar as default if available
          const primaryCalendar = response.data.calendars.find(cal => cal.primary);
          if (primaryCalendar) {
            setSelectedCalendar(primaryCalendar.id);
            setContext(prev => ({ ...prev, selectedCalendar: primaryCalendar.id }));
          }
        }
      } catch (error) {
        console.error('Error fetching calendars:', error);
        addMessage('assistant', 'Sorry, I had trouble connecting to your calendars. Please try again later.');
      }
    };

    const fetchColors = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5000/api/get-calendar-colors');
        if (response.data.success) {
          setColors(response.data.colors);
        }
      } catch (error) {
        console.error('Error fetching colors:', error);
      }
    };

    fetchCalendars();
    fetchColors();
  }, []);

  // Scroll to bottom of messages when new messages are added
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Add a message to the chat
  const addMessage = (type, content) => {
    setMessages(prevMessages => [...prevMessages, { type, content }]);
  };

  // Handle calendar selection
  const handleCalendarSelect = (calendarId) => {
    setSelectedCalendar(calendarId);
    setContext(prev => ({ ...prev, selectedCalendar: calendarId }));

    // Update the user about the calendar change
    const selectedCal = calendars.find(cal => cal.id === calendarId);
    if (selectedCal) {
      addMessage('assistant', `I've switched to the "${selectedCal.summary}" calendar.`);
    }
  };

  // Handle chat submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!inputText.trim()) return;

    // Add user message to chat
    addMessage('user', inputText);

    // Add recent query to context
    const updatedContext = {
      ...context,
      recentQueries: [...context.recentQueries, inputText].slice(-5) // Keep last 5 queries
    };
    setContext(updatedContext);

    // Clear input and show loading
    setInputText("");
    setLoading(true);

    try {
      // Send request to backend
      const response = await axios.post('http://127.0.0.1:5000/api/chat', {
        message: inputText,
        context: updatedContext
      });

      if (response.data.success) {
        // Add response message
        addMessage('assistant', response.data.message);

        // Handle action data based on intent
        const { intent, action_data } = response.data;

        // Handle different intents with visual components
        if (intent === 'create_event' && action_data?.event_created) {
          // Add link to created event
          addMessage('system', (
            <div className="event-created">
              <p>Event created successfully!</p>
              {action_data.event_created.eventLink && (
                <a 
                  href={action_data.event_created.eventLink} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="event-link"
                >
                  View in Calendar
                </a>
              )}
            </div>
          ));
        } else if (intent === 'find_free_time' && action_data?.free_slots) {
          // Display free time slots
          addMessage('system', <FreeTimeDisplay freeSlots={action_data.free_slots} />);
        } else if (intent === 'list_events' && action_data?.events) {
          // Display list of events
          addMessage('system', <EventList events={action_data.events} />);
        } else if (intent === 'update_event' && action_data?.event_updated) {
          // Confirm event update
          addMessage('system', (
            <div className="event-updated">
              <p>Event updated successfully!</p>
              {action_data.event_updated.eventLink && (
                <a 
                  href={action_data.event_updated.eventLink} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="event-link"
                >
                  View in Calendar
                </a>
              )}
            </div>
          ));
        } else if (intent === 'delete_event' && action_data?.event_deleted) {
          // Confirm event deletion
          addMessage('system', <div className="event-deleted">Event deleted successfully!</div>);
        }
      } else {
        // Handle error response
        addMessage('assistant', response.data.message || 'Sorry, I encountered an error.');
      }
    } catch (error) {
      console.error('Error sending message:', error);
      addMessage('assistant', 'Sorry, I had trouble processing your request. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Handle input change
  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  // Handle keypress (Enter to submit)
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  return (
    <div className="calendar-chat-container">
      <div className="chat-header">
        <h2>Calendar Assistant</h2>
        <CalendarSelector 
          calendars={calendars} 
          selectedCalendar={selectedCalendar} 
          onSelect={handleCalendarSelect} 
        />
      </div>

      <div className="chat-messages">
        {messages.map((msg, index) => (
          <ChatMessage 
            key={index} 
            message={msg.content} 
            type={msg.type} 
          />
        ))}
        <div ref={messagesEndRef} />
      </div>

      <form className="chat-input-form" onSubmit={handleSubmit}>
        <textarea
          className="chat-input"
          value={inputText}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          placeholder="Ask about your calendar..."
          disabled={loading}
        />
        <button 
          type="submit" 
          className="send-button"
          disabled={loading || !inputText.trim()}
        >
          {loading ? 'Sending...' : 'Send'}
        </button>
      </form>
    </div>
  );
}

export default CalendarChat;
