import React, { useState, useRef, useEffect } from 'react';
import PropTypes from 'prop-types';

// ChatHeader component with bot info
export const ChatHeader = () => {
  return (
    <div className="chat-header bg-white border-b border-gray-200 p-4 flex items-center">
      <div className="w-10 h-10 rounded-full bg-blue-600 flex items-center justify-center text-white mr-3">
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
        </svg>
      </div>
      <div>
        <h3 className="font-medium text-gray-800">Calendar Assistant</h3>
        <p className="text-xs text-gray-500">Always available to help manage your schedule</p>
      </div>
    </div>
  );
};

// ChatMessage component for individual messages
export const ChatMessage = ({ sender, content, isCalendarView = false, children }) => {
  const isBot = sender === 'bot';

  return (
    <div className={`chat-message flex mb-4 ${isBot ? '' : 'justify-end'}`}>
      {isBot && (
        <div className="w-8 h-8 rounded-full bg-blue-600 flex items-center justify-center text-white mr-2 flex-shrink-0">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
          </svg>
        </div>
      )}

      <div className={`
        message-content max-w-3xl 
        ${isBot 
          ? 'bg-white text-gray-800 rounded-tr-xl rounded-br-xl rounded-bl-xl' 
          : 'bg-blue-600 text-white rounded-tl-xl rounded-bl-xl rounded-br-xl ml-2'}
        ${isCalendarView ? 'p-0 overflow-hidden' : 'p-3'}
        shadow-sm
      `}>
        {isCalendarView ? children : <div dangerouslySetInnerHTML={{ __html: content }} />}
      </div>

      {!isBot && (
        <div className="w-8 h-8 rounded-full bg-blue-600 flex items-center justify-center text-white ml-2 flex-shrink-0">
          <span className="text-sm font-bold">You</span>
        </div>
      )}
    </div>
  );
};

ChatMessage.propTypes = {
  sender: PropTypes.oneOf(['user', 'bot']).isRequired,
  content: PropTypes.string,
  isCalendarView: PropTypes.bool,
  children: PropTypes.node
};

// ChatInput component
export const ChatInput = ({ value, onChange, onSubmit, loading, placeholder }) => {
  const inputRef = useRef(null);

  // Focus the input when component mounts
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  // Handle Enter key press
  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      onSubmit(e);
    }
  };

  return (
    <div className="chat-input border-t border-gray-200 p-3 bg-white">
      <form onSubmit={onSubmit} className="flex items-end">
        <div className="relative flex-grow">
          <textarea
            ref={inputRef}
            className="w-full px-4 py-3 text-gray-700 bg-gray-50 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 border border-gray-200"
            rows="1"
            value={value}
            onChange={onChange}
            onKeyDown={handleKeyDown}
            disabled={loading}
            placeholder={placeholder || "Ask your calendar assistant something..."}
            style={{ resize: 'none' }}
            required
          />
        </div>
        <button
          type="submit"
          disabled={loading || !value.trim()}
          className={`
            ml-3 p-3 rounded-full flex items-center justify-center
            ${(!value.trim() || loading) 
              ? 'bg-gray-300 cursor-not-allowed' 
              : 'bg-blue-600 hover:bg-blue-700 text-white shadow'}
          `}
        >
          {loading ? (
            <svg className="animate-spin h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
            </svg>
          ) : (
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-8.707l-3-3a1 1 0 00-1.414 0l-3 3a1 1 0 001.414 1.414L9 9.414V13a1 1 0 102 0V9.414l1.293 1.293a1 1 0 001.414-1.414z" clipRule="evenodd" />
            </svg>
          )}
        </button>
      </form>
    </div>
  );
};

ChatInput.propTypes = {
  value: PropTypes.string.isRequired,
  onChange: PropTypes.func.isRequired,
  onSubmit: PropTypes.func.isRequired,
  loading: PropTypes.bool,
  placeholder: PropTypes.string
};

