import React, { useState } from 'react';

// Modern Header Component
export const Header = () => {
  return (
    <header className="bg-gradient-to-r from-blue-600 to-indigo-700 text-white shadow-lg">
      <div className="container mx-auto px-4 py-6 flex justify-between items-center">
        <div className="flex items-center space-x-2">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
          </svg>
          <h1 className="text-2xl font-bold">ScheduleSync</h1>
        </div>
        <nav>
          <ul className="flex space-x-6">
            <li><a href="#" className="hover:text-blue-200 transition-colors duration-200">Home</a></li>
            <li><a href="#" className="hover:text-blue-200 transition-colors duration-200">My Calendar</a></li>
            <li><a href="#" className="hover:text-blue-200 transition-colors duration-200">Settings</a></li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

// Calendar Selector Component with Modern UI
export const ModernCalendarSelector = ({ calendars, selected, onSelect, disabled }) => {
  return (
    <div className="bg-white rounded-xl shadow-md p-6 mb-6">
      <h3 className="text-lg font-semibold text-gray-800 mb-4">My Calendars</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
        {calendars.length > 0 ? (
          calendars.map((cal) => (
            <div 
              key={cal.id} 
              className={`
                flex items-center p-3 rounded-lg transition-all duration-200
                ${selected.some(s => s.id === cal.id) 
                  ? 'bg-blue-50 border border-blue-200' 
                  : 'bg-gray-50 border border-gray-200 hover:bg-gray-100'}
                ${disabled ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'}
              `}
              onClick={() => !disabled && onSelect(cal)}
            >
              <input
                type="checkbox"
                className="form-checkbox h-5 w-5 text-blue-600 rounded transition duration-150 ease-in-out"
                checked={selected.some((s) => s.id === cal.id)}
                onChange={() => {}}
                disabled={disabled}
              />
              <div className="ml-3 flex items-center">
                <div 
                  className="w-3 h-3 rounded-full mr-2" 
                  style={{ backgroundColor: cal.backgroundColor || '#4285f4' }}
                ></div>
                <span className={`
                  ${cal.primary ? 'font-medium text-blue-700' : 'text-gray-700'}
                `}>
                  {cal.summary} {cal.primary ? '(Primary)' : ''}
                </span>
              </div>
            </div>
          ))
        ) : (
          <div className="col-span-2 py-4 text-center text-gray-500 bg-gray-50 rounded-lg">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-10 w-10 mx-auto mb-2 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
            </svg>
            <p>Loading calendars...</p>
          </div>
        )}
      </div>
    </div>
  );
};

