import React, { useState } from 'react';
import axios from 'axios';

function NaturalLanguageForm() {
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;

    setLoading(true);
    setError("");
    setMessage("");

    try {
      const response = await axios.post('http://127.0.0.1:5000/api/calendar-command', { text });

      if (response.data.success) {
        if (response.data.events) {
          // Handle LIST intent (display events)
          setMessage("Your events: " + response.data.events.map(event => event.title).join(", "));
        } else if (response.data.eventLink) {
          // Handle CREATE intent
          setMessage(`Event created! View it here: ${response.data.eventLink}`);
          setText(""); // Clear input only for creation
        } else {
          // Handle UPDATE or DELETE intents
          setMessage(response.data.message);
        }
      } else {
        setError(`Error: ${response.data.message}`);
      }
    } catch (error) {
      setError(`Error: ${error.response?.data?.message || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="natural-language-form">
      <h2>Talk to Your Calendar</h2>

      {error && <div className="error-message">{error}</div>}
      {message && <div className="success-message">{message}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Describe what you want:</label>
          <textarea
            placeholder={`Examples:
- Schedule a meeting with John tomorrow at 2 PM
- Show my events for next week
- Move my 3 PM meeting to 4 PM
- Cancel my dentist appointment`}
            value={text}
            onChange={(e) => setText(e.target.value)}
            disabled={loading}
            rows={3}
            required
          />
        </div>

        <button type="submit" disabled={loading} className="submit-button">
          {loading ? 'Processing...' : 'Submit'}
        </button>
      </form>
    </div>
  );
}

export default NaturalLanguageForm;
