import React, { useState, useEffect } from 'react';
import axios from 'axios';


function CalendarForm() {
  // Get current date and time in ISO format
  const getCurrentDateTime = () => {
    const now = new Date();
    // Format for input datetime-local: YYYY-MM-DDThh:mm
    return now.toISOString().slice(0, 16);
  };

  const [eventData, setEventData] = useState({
    summary: 'My Event',
    location: '',
    description: '',
    start: {
      dateTime: getCurrentDateTime(),
      timeZone: Intl.DateTimeFormat().resolvedOptions().timeZone,
    },
    end: {
      dateTime: '',
      timeZone: Intl.DateTimeFormat().resolvedOptions().timeZone,
    },
    calendarId: 'primary', // Default to primary calendar
    duration: '01:00', // Default 1 hour duration
    colorId: '' // Will be set based on calendar
  });

  const [calendars, setCalendars] = useState([]);
  const [calendarColors, setCalendarColors] = useState({});
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  // Recurrence state
  const [recurrenceType, setRecurrenceType] = useState('none');
  const [recurrenceFreq, setRecurrenceFreq] = useState('DAILY');
  const [recurrenceCount, setRecurrenceCount] = useState(3);
  const [recurrenceDays, setRecurrenceDays] = useState([]);

  // Update recurrence rules when settings change
  useEffect(() => {
    if (recurrenceType === 'none') {
      setEventData(prev => ({ ...prev, recurrence: null }));
      return;
    }

    let rrule = `RRULE:FREQ=${recurrenceFreq}`;

    if (recurrenceCount) {
      rrule += `;COUNT=${recurrenceCount}`;
    }

    if (recurrenceFreq === 'WEEKLY' && recurrenceDays.length > 0) {
      rrule += `;BYDAY=${recurrenceDays.join(',')}`;
    }

    setEventData(prev => ({ ...prev, recurrence: [rrule] }));
  }, [recurrenceType, recurrenceFreq, recurrenceCount, recurrenceDays]);

  // Fetch available calendars and colors when component mounts
  useEffect(() => {
    const fetchCalendarsAndColors = async () => {
      try {
        setError('');
        setLoading(true);

        // Fetch calendars
        const calendarResponse = await axios.get('http://127.0.0.1:5000/api/get-calendars');
        if (calendarResponse.data.success) {
          setCalendars(calendarResponse.data.calendars);

          // If there's a primary calendar, set it as default
          const primaryCalendar = calendarResponse.data.calendars.find(cal => cal.primary);
          if (primaryCalendar) {
            setEventData(prev => ({
              ...prev,
              calendarId: primaryCalendar.id,
              colorId: primaryCalendar.colorId // Set event color to match calendar
            }));
          }
        } else {
          setError(`Error: ${calendarResponse.data.message}`);
        }

        // Fetch colors
        const colorsResponse = await axios.get('http://127.0.0.1:5000/api/get-calendar-colors');
        if (colorsResponse.data.success) {
          setCalendarColors(colorsResponse.data.colors);
        } else {
          setError(`Error: ${colorsResponse.data.message}`);
        }
      } catch (error) {
        setError(`Error fetching data: ${error.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchCalendarsAndColors();
  }, []);

  // Format date for ISO 8601 format that Google Calendar API expects
  const formatDateForAPI = (dateString) => {
    try {
      // If it's already in ISO format, return as is
      if (dateString.includes('T') && dateString.includes('-')) {
        return dateString;
      }

      // Create a date object from the string
      const date = new Date(dateString);

      // Format to ISO string
      return date.toISOString();
    } catch (error) {
      console.error("Date formatting error:", error);
      return dateString;
    }
  };

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === 'calendarId') {
      const selectedCalendar = calendars.find(cal => cal.id === value);
      setEventData({
        ...eventData,
        calendarId: value,
        colorId: selectedCalendar?.colorId // Auto-match event color to calendar
      });
    } else {
      setEventData({
        ...eventData,
        [name]: value
      });
    }
  };

  // Handle date/time changes
  const handleDateTimeChange = (e) => {
    const { name, value } = e.target;
    const [field, subfield] = name.split('.');

    setEventData({
      ...eventData,
      [field]: {
        ...eventData[field],
        [subfield]: value
      }
    });
  };

  // Submit form to Flask backend
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setMessage('');

    try {
      // Create a copy of data to manipulate
      const submissionData = JSON.parse(JSON.stringify(eventData));

      // Format dates for the API
      if (submissionData.start && submissionData.start.dateTime) {
        submissionData.start.dateTime = formatDateForAPI(submissionData.start.dateTime);
      }

      // Send data to Flask API
      const response = await axios.post('http://127.0.0.1:5000/api/create-event', submissionData);

      if (response.data.success) {
        setMessage(`Event created! View it here: ${response.data.eventLink}`);
        // Reset form or keep as is depending on your UX preference
      } else {
        setError(`Error: ${response.data.message}`);
      }
    } catch (error) {
      setError(`Error: ${error.response?.data?.message || error.message}`);
      console.error("Form submission error:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="calendar-form">
      <h2>Create Calendar Event</h2>

      {error && <div className="error-message">{error}</div>}
      {message && <div className="success-message">{message}</div>}

      <form onSubmit={handleSubmit}>
        {/* Calendar Selection Dropdown */}
        <div className="form-group">
          <label>Calendar:</label>
          <select 
            name="calendarId" 
            value={eventData.calendarId} 
            onChange={handleChange}
            disabled={loading}
          >
            {calendars.map(calendar => (
              <option 
                key={calendar.id} 
                value={calendar.id}
              >
                {calendar.summary} {calendar.primary ? '(Primary)' : ''}
              </option>
            ))}
          </select>
        </div>

        {/* Event Title */}
        <div className="form-group">
          <label>Title:</label>
          <input 
            type="text" 
            name="summary" 
            value={eventData.summary} 
            onChange={handleChange}
            disabled={loading}
            required
          />
        </div>

        {/* Event Location */}
        <div className="form-group">
          <label>Location:</label>
          <input 
            type="text" 
            name="location" 
            value={eventData.location} 
            onChange={handleChange}
            disabled={loading}
          />
        </div>

        {/* Event Description */}
        <div className="form-group">
          <label>Description:</label>
          <textarea 
            name="description" 
            value={eventData.description} 
            onChange={handleChange}
            disabled={loading}
          />
        </div>

        {/* Event Start Date/Time */}
        <div className="form-group">
          <label>Start Date and Time:</label>
          <input 
            type="datetime-local" 
            name="start.dateTime" 
            value={eventData.start.dateTime} 
            onChange={handleDateTimeChange}
            disabled={loading}
            required
          />
        </div>

        {/* Event Duration */}
        <div className="form-group">
          <label>Duration (HH:MM):</label>
          <input 
            type="text" 
            name="duration" 
            placeholder="HH:MM"
            pattern="[0-9]{1,2}:[0-9]{2}"
            title="Format: HH:MM (e.g., 01:30 for 1 hour and 30 minutes)"
            value={eventData.duration} 
            onChange={handleChange}
            disabled={loading}
            required
          />
        </div>

        {/* Event Color */}
        <div className="form-group">
          <label>Event Color:</label>
          <select 
            name="colorId" 
            value={eventData.colorId || ''} 
            onChange={handleChange}
            disabled={loading}
            style={{ 
              backgroundColor: calendarColors?.event?.[eventData.colorId]?.background || 'white',
              color: calendarColors?.event?.[eventData.colorId]?.foreground || 'black'
            }}
          >
            <option value="">Use calendar color</option>
            {calendarColors?.event && Object.entries(calendarColors.event).map(([id, color]) => (
              <option 
                key={id} 
                value={id}
                style={{ 
                  backgroundColor: color.background, 
                  color: color.foreground 
                }}
              >
                Color {id}
              </option>
            ))}
          </select>
        </div>

        {/* Event Repetition */}
        <div className="form-group">
          <label>Repetition:</label>
          <select 
            value={recurrenceType} 
            onChange={(e) => setRecurrenceType(e.target.value)}
            disabled={loading}
          >
            <option value="none">No repeat</option>
            <option value="repeat">Repeat</option>
          </select>

          {recurrenceType === 'repeat' && (
            <div className="recurrence-options">
              <div className="form-group">
                <label>Frequency:</label>
                <select 
                  value={recurrenceFreq} 
                  onChange={(e) => setRecurrenceFreq(e.target.value)}
                  disabled={loading}
                >
                  <option value="DAILY">Daily</option>
                  <option value="WEEKLY">Weekly</option>
                  <option value="MONTHLY">Monthly</option>
                  <option value="YEARLY">Yearly</option>
                </select>
              </div>

              <div className="form-group">
                <label>Repeat:</label>
                <input 
                  type="number" 
                  min="1" 
                  max="99"
                  value={recurrenceCount} 
                  onChange={(e) => setRecurrenceCount(e.target.value)}
                  disabled={loading}
                /> times
              </div>

              {recurrenceFreq === 'WEEKLY' && (
                <div className="weekday-selector">
                  <label>On days:</label>
                  <div className="weekday-checkboxes">
                    {[
                      {day: 'MO', label: 'Monday'},
                      {day: 'TU', label: 'Tuesday'},
                      {day: 'WE', label: 'Wednesday'},
                      {day: 'TH', label: 'Thursday'},
                      {day: 'FR', label: 'Friday'},
                      {day: 'SA', label: 'Saturday'},
                      {day: 'SU', label: 'Sunday'}
                    ].map(({day, label}) => (
                      <label key={day} className="weekday-checkbox">
                        <input 
                          type="checkbox" 
                          checked={recurrenceDays.includes(day)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setRecurrenceDays([...recurrenceDays, day]);
                            } else {
                              setRecurrenceDays(recurrenceDays.filter(d => d !== day));
                            }
                          }}
                          disabled={loading}
                        />
                        {label}
                      </label>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}
        </div>

        <button type="submit" disabled={loading} className="submit-button">
          {loading ? 'Creating...' : 'Create Event'}
        </button>
      </form>
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <CalendarForm />
    </div>
  );
}

export default App;
